import React, { useState } from "react";

function HookArray() {
  const [todoList, SetTodoList] = useState({ id: "", title: "" });

  const initialID = 0;

  const handleChange = (e) => {
    SetTodoList({ ...todoList, id: initialID + 1, title: e.target.value });
  };

  const handleSubmit = () => {
    alert(`Hello ${todoList.title}`);
  };

  return (
    <div>
      <input type="text" value={todoList.title} onChange={handleChange} />
      <button onClick={handleSubmit}>Add Task</button>
    </div>
  );
}

export default HookArray;
