import React, { useState } from "react";

function HookArray() {
  const arraytt = ["sayo", "tope"];
  const [todoList, SetTodoList] = useState([(title: "")]);

  const initialID = 0;

  const handleChange = (e) => {
    SetTodoList({ ...todoList, id: initialID + 1, title: e.target.value });
    todoList.push(todoList.title);
  };

  const handleSubmit = () => {
    // alert(`Hello ${todoList.title}`);
    SetTodoList({ ...todoList, title: todoList.title });
  };

  return (
    <div>
      <input type="text" value={todoList.title} onChange={handleChange} />
      <button onClick={handleSubmit}>Add Task</button>

      <p key={todoList.id}>{todoList.title}</p>
    </div>
  );
}

export default HookArray;
