import React, { useState } from "react";

function Home() {
  const [searchword, SetSearchWord] = useState("");

  const searchHandler = (e) => {
    SetSearchWord(
      {
        searchword: e.target.value,
      },
      alert(searchword)
    );
  };
  return (
    <div>
      <input type="text" value={searchword} />
      <button onClick={searchHandler}>Search</button>
    </div>
  );
}

export default Home;
