import React, { useState, useEffect } from "react";
import axios from "axios";

function Home() {
  const [searchword, SetSearchWord] = useState("");

  const searchHandler = (e) => {
    e.preventDefault();

    useEffect = () => {
      axios.get(`https://api.github.com/users/${searchword}`);
    };
    SetSearchWord({}, alert(searchword));
  };
  return (
    <div>
      <input type="text" value={searchword} onChange={(e) => e.target.value} />
      <button onClick={searchHandler}>Search</button>
    </div>
  );
}

export default Home;
