import React, { useState } from "react";
import TodoDetails from "./TodoDetails";
import "bootstrap/dist/css/bootstrap.css";

function CrudOp() {
  const todoLists = [
    { id: 1, title: "Read", details: "I will love to read today" },
    { id: 2, title: "Dance", details: "I will love to dance today" },
    { id: 3, title: "Jump", details: "I will love to jump today" },
    { id: 4, title: "Sing", details: "I will love to sing today" },
    { id: 5, title: "Stand", details: "I will love to stand today" },
    { id: 6, title: "Code", details: "I will love to code today" },
    { id: 7, title: "Sleep", details: "I will love to sleep today" },
  ];

  //   const todoLists = {
  //     id: null,
  //     title: "",
  //     details: "",
  //   };

  const [todos, setTodos] = useState(todoLists);

  const addDetails = (todo) => {
    todo.id = todoLists.length + 1;
    setTodos([...todoLists, todo]);
  };

  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setTodos(
      {
        ...todos,
        [name]: value,
      },
      [name, value]
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addDetails(todos);
    // setTodos(todos);
    console.log(todos.id);
  };

  return (
    <div className="container">
      <h1>My Note App</h1>
      <div className="flex-row">
        <div className="flex-large">
          <h2>Add Todo</h2>
          <form onSubmit={handleSubmit}>
            <label>Title</label>
            <input
              type="text"
              name="title"
              value={todos.title}
              onChange={onChangeInput}
            />
            <label>Details</label>
            <input
              type="text"
              name="details"
              value={todos.details}
              onChange={onChangeInput}
            />
            <button type="submit">Add New Task</button>
          </form>
        </div>
        <div className="flex-large">
          <h2>View Todo(s)</h2>
          <TodoDetails todos={todos} />
        </div>
      </div>
    </div>
  );
}

export default CrudOp;
