import React, { useState } from "react";

const EditTaskForm = (props) => {
  const [todo, setTodo] = useState(prop.currentTask);

  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setTodo({
      ...todo,
      [name]: value,
    });
  };
  return (
    <div>
      <form
        onSubmit={(e) => {
          props.updateTask(todo.id, todo);
        }}
      >
        <label>Title</label>
        <input
          type="text"
          name="title"
          value={todo.title}
          onChange={onChangeInput}
        />
        <label>Details</label>
        <input
          type="text"
          name="details"
          value={todo.details}
          onChange={onChangeInput}
        />
        <button type="submit">Update Task</button>
        <button
          onClick={() => props.setEditing(false)}
          className="button mutted-button"
          type="submit"
        >
          Cancel
        </button>
      </form>
    </div>
  );
};

export default EditTaskForm;
