import React, { useState } from "react";

function HookArray() {
  const postList = [];
  const [todoLists, setTodoLists] = useState({
    id: "",
    title: "",
    details: "",
  });

  const submitHandler = () => {
    console.log(todoLists.title + todoLists.details);
  };
  return (
    <div>
      <input
        type="text"
        value={todoLists.title}
        onChange={(e) => setTodoLists({ ...todoLists, title: e.target.value })}
      />
      <textarea
        value={todoLists.details}
        onChange={(e) =>
          setTodoLists({ ...todoLists, details: e.target.value })
        }
        cols="5"
      />
      <button onClick={submitHandler}>Add new task</button>
    </div>
  );
}

export default HookArray;
