import React, { useState } from "react";

function HookArray() {
  // const arraytt = [];
  const [todoList, SetTodoList] = useState([]);
  const [title, setTitle] = useState("");

  const initialID = 0;

  const handleChange = (e) => {
    SetTodoList({ value: e.target.value });
  };

  const handleSubmit = () => {
    SetTodoList(
      [
        ...todoList,
        {
          id: todoList.length,
          value: title,
        },
      ],
      console.log(title)
    );
  };

  return (
    <div>
      <input
        type="text"
        onChange={(e) => setTitle({ ...todoList, title: e.target.value })}
      />
      <button onClick={handleSubmit}>Add Task</button>
      {todoList.map((todo) => (
        <p key={todo.id}>
          {todo.id}
          {todo.value.title}
        </p>
      ))}
      {/* <p key={todoList.id}>{todoList.title}</p> */}
    </div>
  );
}

export default HookArray;
