import React, { useState } from "react";

function HookArray() {
  const [todoLists, setTodoList] = useState([]);

  const todoListsHandler = () => {
    setTodoList([
      ...todoLists,
      {
        id: todoLists.length(),
        value: todoLists.value,
      },
    ]);
  };

  return (
    <div>
      <input
        type="text"
        value={todoLists}
        onChange={() =>
          setTodoList((e) => {
            todoLists.value = e.target.value;
          })
        }
      />
      <button onClick={todoListsHandler}>Add Todo</button>
      todoLists.map(
      {(todoList) => <h1 key={todoLists.id}> {todoLists.value}</h1>})
    </div>
  );
}

export default HookArray;
