import React, { useState } from "react";

function HookArray() {
  const arraytt = [];
  const [todoList, SetTodoList] = useState({ title: "" });

  const initialID = 0;

  const handleChange = (e) => {
    SetTodoList({ ...todoList, id: initialID + 1, title: e.target.value }, [
      title,
    ]);
  };

  const handleSubmit = () => {
    // alert(`Hello ${todoList.title}`);

    arraytt.push(todoList.title.toString());

    alert(arraytt.toString());
  };

  return (
    <div>
      <input type="text" onChange={handleChange} />
      <button onClick={handleSubmit}>Add Task</button>

      {/* <p key={todoList.id}>{todoList.title}</p> */}
    </div>
  );
}

export default HookArray;
