import React, { useState } from "react";

function HookArray() {
  const [postList, setPostList] = useState([]);
  // const [todoLists, setTodoLists] = useState({
  //   id: "",
  //   title: "",
  //   details: "",
  // });
  const [todoLists, setTodoLists] = useState([]);

  const submitHandler = (e) => {
    setTodoLists([
      ...todoLists,
      {
        id: todoLists.length,
        title: e.current.value,
        details: e.current.value,
      },
    ]);
    console.log(todoLists);
  };
  return (
    <div>
      <input
        type="text"
        value={todoLists.title}
        onChange={(e) => setTodoLists({ ...todoLists, title: e.target.value })}
      />
      <textarea
        value={todoLists.details}
        onChange={(e) =>
          setTodoLists({ ...todoLists, details: e.target.value })
        }
        cols="5"
      />
      <button onClick={submitHandler}>Add new task</button>
      {/* {postList.length} ? */}
      <ul>
        {todoLists.map((post) => (
          <li key={post.id}>{post.title}</li>
        ))}
      </ul>
      {/* : <h1>No data</h1> */}
    </div>
  );
}

export default HookArray;
