import React, { useState } from "react";

function HookArray() {
  const [todoLists, setTodoList] = useState([]);
  const [name, setName] = useState("");

  const todoListsHandler = (e) => {
    setTodoList([
      ...todoLists,
      {
        id: todoLists.length,
        value: Math.floor(Math.random() * 10) + 1,
        // name: e.target.name,
      },
    ]);
  };
  const changeName = (e) => {
    setName({
      name: e.target.value,
    });
  };

  return (
    <div>
      {/* <input
        type="text"
        value={todoLists.name}
        onChange={(e) =>
          setTodoList([...todoLists, (todoLists.name = e.target.value)])
        }
      /> */}
      <input type="text" value={todoLists.name} onChange={changeName} />
      <button onClick={todoListsHandler}>Add Todo</button>

      {todoLists.map((todoList) => (
        <h1 key={todoList.id}>{todoList.name}</h1>
      ))}
    </div>
  );
}

export default HookArray;
