import React, { useState } from "react";

function HookArray() {
  const [todoLists, setTodoList] = useState([]);

  const todoListsHandler = () => {
    setTodoList([
      ...todoLists,
      {
        id: todoLists.length,
        // value: Math.floor(Math.random() * 10) + 1,
        name: todoLists.name,
      },
    ]);
  };

  return (
    <div>
      {/* <input
        type="text"
        value={todoLists.name}
        onChange={() =>
          setTodoList((e) => [...todoLists, (todoLists.name = e.target.value)])
        }
      /> */}
      <input
        type="text"
        value={todoLists.name}
        onChange={(e) => setTodoList({ ...todoLists, name: e.target.value })}
      />
      <button onClick={todoListsHandler}>Add Todo</button>
      <h1>{todoLists.name}</h1>

      {/* {todoLists.map((todoList) => (
        <h1 key={todoList.id}>
          {todoList.id} {todoList.name}
        </h1>
      ))} */}
    </div>
  );
}

export default HookArray;
