import React, { useState } from "react";

const AddTask = (props) => {
  const todoLists = {
    id: null,
    title: "",
    details: "",
  };
  const [todos, setTodos] = useState(todoLists);
  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setTodos({
      ...todos,
      [name]: value,
    });
  };

  //   const handleSubmit = (e) => {
  //     e.preventDefault();
  //     if (!todos.title || !todos.details) return;
  //     props.addDetails(todos);
  //     // setTodos(todos);
  //     console.log(todos.id);
  //   };

  const addDetails = (todo) => {
    todo.id = todos.length + 1;
    setTodos([...todos, todo]);
  };

  const [editTask, SetEditTask] = useState(false);
  const intialTaskState = { id: null, title: "", details: "" };
  const [currentTask, setCurrentTask] = useState(intialTaskState);
  return (
    <div>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          if (!todos.title || !todos.details) return;
          props.addDetails(todos);
          // setTodos(todos);
          console.log(todos.id);
        }}
      >
        <label>Title</label>
        <input
          type="text"
          name="title"
          value={todos.title}
          onChange={onChangeInput}
        />
        <label>Details</label>
        <input
          type="text"
          name="details"
          value={todos.details}
          onChange={onChangeInput}
        />
        <button type="submit">Add New Task</button>
      </form>
    </div>
  );
};

export default AddTask;
