import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.css";

function HookArray() {
  // const arraytt = [];
  const [todoList, SetTodoList] = useState([]);
  const [title, setTitle] = useState("");
  const [details, setDetails] = useState("");

  const handleSubmit = () => {
    SetTodoList([
      ...todoList,
      {
        id: todoList.length,
        value: title,
        todoDetails: details,
      },
    ]);
  };

  return (
    <div className="card">
      {/* <input
        type="text"
        onChange={(e) => setTitle({ ...todoList, title: e.target.value })}
      />
      <textarea
        onChange={(e) => setDetails({ ...todoList, details: e.target.value })}
      />
      <button onClick={handleSubmit}>Add Task</button> */}

      <h5 className="card-header info-color white-text text-center py-4">
        <strong>Task Chart</strong>
      </h5>

      <div className="card-body px-lg-5 pt-0">
        <form className="text-center" action="#!">
          <div className="md-form">
            <input
              type="text"
              id="title"
              class="form-control"
              onChange={(e) => setTitle({ ...todoList, title: e.target.value })}
            />
            <label for="title">Title</label>
          </div>

          <div className="md-form">
            <input
              type="password"
              id="details"
              class="form-control"
              onChange={(e) =>
                setDetails({ ...todoList, details: e.target.value })
              }
            />
            <label for="materialLoginFormPassword">Details</label>
          </div>

          <div className="d-flex justify-content-around">
            <button
              className="btn btn-outline-info btn-rounded btn-block my-4 waves-effect z-depth-0"
              type="submit"
              onClick={handleSubmit}
            >
              Sign in
            </button>
          </div>
        </form>
      </div>
      {todoList.map((todo) => (
        <div class="card border-primary mb-1" key={todo.id}>
          <div class="card-header">{todo.value.title}</div>
          <div class="card-body text-primary">
            <h5 class="card-title">{todo.todoDetails.details}</h5>
            <p class="card-text"></p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default HookArray;
