import React, { useState } from "react";

function HookArray() {
  // const arraytt = [];
  const [todoList, SetTodoList] = useState([]);
  const [title, setTitle] = useState("");

  const initialID = 0;

  const handleChange = (e) => {
    SetTodoList({ value: e.target.value });
  };

  const handleSubmit = (e) => {
    SetTodoList([
      ...todoList,
      {
        id: todoList.length,
        value: todoList.title,
      },
    ]);

    // alert(`Hello ${todoList.title}`);

    // arraytt.push(todoList.title.toString());

    // alert(arraytt.toString());
  };

  return (
    <div>
      <input type="text" onChange={() => setTitle(e.target.value)} />
      <button onClick={handleSubmit}>Add Task</button>
      {todoList.map((todo) => (
        <p key={todo.id}>{todo.value}</p>
      ))}
      {/* <p key={todoList.id}>{todoList.title}</p> */}
    </div>
  );
}

export default HookArray;
