import React from "react";

function HookArray() {
  const [todoLists, setTodoLists] = useState({
    id: "",
    title: "",
    details: "",
  });

  const submitHandler = () => {};
  return (
    <div>
      <input
        type="text"
        value={todoLists.title}
        onChange={(e) => setTodoLists({ title: e.target.value })}
      />
      <textarea
        value={todoLists.details}
        onChange={(e) => setTodoLists({ title: e.target.value })}
        cols="5"
      />
      <button onClick={submitHandler}>Add new task</button>
    </div>
  );
}

export default HookArray;
