import React, { useState, useEffect } from "react";
import axios from "axios";

function Home() {
  const [searchword, SetSearchWord] = useState("");
  const [result, setResult] = useState("");
  const [errormsg, setErrorMsg] = useState("");

  useEffect = () => {
    axios
      // .get(`https://api.github.com/users/${searchword}`)
      .get(`https://api.github.com/users/temilolasayo`)
      .then((response) => {
        setResult({
          result: response.data,
        });
      })
      .catch((error) => {
        setErrorMsg({
          errormsg: "Something Went wrong!!!!",
        });
      });
  };
  // SetSearchWord({}, alert(url));

  return (
    <div>
      <input type="text" value={searchword} onChange={(e) => e.target.value} />
      <p>{result}</p>
    </div>
  );
}

export default Home;
